import React, { useState, useEffect } from 'react';
import { Report, User } from '../../types';

interface ReportFilters {
  status: string;
  wasteType: string;
  dateFrom: string;
  dateTo: string;
  search: string;
}

const ReportManagement: React.FC = () => {
  const [reports, setReports] = useState<Report[]>([]);
  const [collectors, setCollectors] = useState<User[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [selectedReport, setSelectedReport] = useState<Report | null>(null);
  const [showAssignModal, setShowAssignModal] = useState(false);
  const [filters, setFilters] = useState<ReportFilters>({
    status: '',
    wasteType: '',
    dateFrom: '',
    dateTo: '',
    search: ''
  });

  const wasteTypes = ['Plastic', 'Paper', 'Glass', 'Metal', 'Organic', 'Electronic', 'Hazardous', 'Mixed'];
  const statuses = ['pending', 'verified', 'assigned', 'in-progress', 'completed', 'rejected'];

  useEffect(() => {
    fetchReports();
    fetchCollectors();
  }, [filters]);

  const fetchReports = async () => {
    try {
      setLoading(true);
      const queryParams = new URLSearchParams();

      Object.entries(filters).forEach(([key, value]) => {
        if (value) queryParams.append(key, value);
      });

      const response = await apiClient.get(`/reports?${queryParams.toString()}`);
      setReports((response.data as any).reports || response.data || []);
    } catch (err: any) {
      setError(err.response?.data?.message || 'Failed to fetch reports');
    } finally {
      setLoading(false);
    }
  };

  const fetchCollectors = async () => {
    try {
      const response = await apiClient.get('/users?role=collector&status=available');
      setCollectors((response.data as any).users || response.data || []);
    } catch (err: any) {
      console.error('Failed to fetch collectors:', err);
    }
  };

  const handleStatusUpdate = async (reportId: string, newStatus: string) => {
    try {
      await apiClient.put(`/reports/${reportId}/status`, { status: newStatus });
      fetchReports();
    } catch (err: any) {
      alert(err.response?.data?.message || 'Failed to update status');
    }
  };

  const handleAssignCollector = async (collectorId: string) => {
    if (!selectedReport) return;

    try {
      await apiClient.put(`/reports/${selectedReport._id}/assign`, { collectorId });
      setShowAssignModal(false);
      setSelectedReport(null);
      fetchReports();
    } catch (err: any) {
      alert(err.response?.data?.message || 'Failed to assign collector');
    }
  };

  const getStatusBadge = (status: string) => {
    const statusClasses = {
      pending: 'status-pending',
      verified: 'status-verified',
      assigned: 'status-assigned',
      'in-progress': 'status-in-progress',
      completed: 'status-completed',
      rejected: 'status-rejected'
    };
    return statusClasses[status as keyof typeof statusClasses] || 'status-pending';
  };

  const getPriorityBadge = (priority: string) => {
    const priorityClasses = {
      low: 'bg-green-100 text-green-800',
      medium: 'bg-yellow-100 text-yellow-800',
      high: 'bg-red-100 text-red-800'
    };
    return priorityClasses[priority as keyof typeof priorityClasses] || 'bg-gray-100 text-gray-800';
  };

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex justify-between items-center">
        <h1 className="text-3xl font-bold text-gray-900">Report Management</h1>
        <button
          onClick={fetchReports}
          className="btn-secondary"
        >
          Refresh
        </button>
      </div>

      {/* Filters */}
      <div className="card">
        <div className="card-body">
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-5 gap-4">
            <div>
              <label className="form-label">Status</label>
              <select
                value={filters.status}
                onChange={(e) => setFilters({ ...filters, status: e.target.value })}
                className="form-input"
              >
                <option value="">All Statuses</option>
                {statuses.map(status => (
                  <option key={status} value={status}>
                    {status.charAt(0).toUpperCase() + status.slice(1)}
                  </option>
                ))}
              </select>
            </div>

            <div>
              <label className="form-label">Waste Type</label>
              <select
                value={filters.wasteType}
                onChange={(e) => setFilters({ ...filters, wasteType: e.target.value })}
                className="form-input"
              >
                <option value="">All Types</option>
                {wasteTypes.map(type => (
                  <option key={type} value={type}>{type}</option>
                ))}
              </select>
            </div>

            <div>
              <label className="form-label">From Date</label>
              <input
                type="date"
                value={filters.dateFrom}
                onChange={(e) => setFilters({ ...filters, dateFrom: e.target.value })}
                className="form-input"
              />
            </div>

            <div>
              <label className="form-label">To Date</label>
              <input
                type="date"
                value={filters.dateTo}
                onChange={(e) => setFilters({ ...filters, dateTo: e.target.value })}
                className="form-input"
              />
            </div>

            <div>
              <label className="form-label">Search</label>
              <input
                type="text"
                placeholder="Search reports..."
                value={filters.search}
                onChange={(e) => setFilters({ ...filters, search: e.target.value })}
                className="form-input"
              />
            </div>
          </div>
        </div>
      </div>

      {/* Reports Table */}
      <div className="card">
        <div className="card-body p-0">
          {loading ? (
            <div className="flex items-center justify-center h-64">
              <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-green-600"></div>
            </div>
          ) : error ? (
            <div className="p-6 bg-red-50 border border-red-200 rounded-lg">
              <p className="text-red-600">{error}</p>
            </div>
          ) : (
            <div className="overflow-x-auto">
              <table className="min-w-full divide-y divide-gray-200">
                <thead className="bg-gray-50">
                  <tr>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Report Details
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Location
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Status
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Priority
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Assigned To
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Actions
                    </th>
                  </tr>
                </thead>
                <tbody className="bg-white divide-y divide-gray-200">
                  {reports.map((report) => (
                    <tr key={report._id} className="hover:bg-gray-50">
                      <td className="px-6 py-4 whitespace-nowrap">
                        <div>
                          <p className="text-sm font-medium text-gray-900">{report.wasteType}</p>
                          <p className="text-sm text-gray-500">
                            By: {report.citizenName || 'Unknown'}
                          </p>
                          <p className="text-xs text-gray-400">
                            {new Date(report.createdAt).toLocaleDateString()}
                          </p>
                        </div>
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap">
                        <div className="text-sm text-gray-900">
                          {report.location.address}
                        </div>
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap">
                        <span className={`${getStatusBadge(report.status)}`}>
                          {report.status}
                        </span>
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap">
                        <span className={`px-2 py-1 rounded-full text-xs font-medium ${getPriorityBadge(report.priority)}`}>
                          {report.priority}
                        </span>
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                        {report.assignedToName || 'Unassigned'}
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm font-medium space-x-2">
                        {report.status === 'submitted' && (
                          <>
                            <button
                              onClick={() => handleStatusUpdate(report._id, 'verified')}
                              className="text-green-600 hover:text-green-900"
                            >
                              Verify
                            </button>
                            <button
                              onClick={() => handleStatusUpdate(report._id, 'rejected')}
                              className="text-red-600 hover:text-red-900"
                            >
                              Reject
                            </button>
                          </>
                        )}
                        {report.status === 'verified' && !report.assignedTo && (
                          <button
                            onClick={() => {
                              setSelectedReport(report);
                              setShowAssignModal(true);
                            }}
                            className="text-blue-600 hover:text-blue-900"
                          >
                            Assign
                          </button>
                        )}
                        <button className="text-gray-600 hover:text-gray-900">
                          View
                        </button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
              {reports.length === 0 && (
                <div className="text-center py-12">
                  <p className="text-gray-500">No reports found</p>
                </div>
              )}
            </div>
          )}
        </div>
      </div>

      {/* Assign Collector Modal */}
      {showAssignModal && selectedReport && (
        <div className="fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full z-50">
          <div className="relative top-20 mx-auto p-5 border w-96 shadow-lg rounded-md bg-white">
            <div className="mt-3">
              <h3 className="text-lg font-medium text-gray-900 mb-4">
                Assign Collector
              </h3>
              <p className="text-sm text-gray-600 mb-4">
                Report: {selectedReport.wasteType} at {selectedReport.location.address}
              </p>
              <div className="space-y-2 max-h-60 overflow-y-auto">
                {collectors.map((collector) => (
                  <button
                    key={collector._id}
                    onClick={() => handleAssignCollector(collector._id)}
                    className="w-full text-left p-3 border rounded-lg hover:bg-gray-50"
                  >
                    <p className="font-medium">{collector.name}</p>
                    <p className="text-sm text-gray-600">{collector.email}</p>
                    <p className="text-xs text-gray-500">
                      Phone: {collector.phone}
                    </p>
                  </button>
                ))}
                {collectors.length === 0 && (
                  <p className="text-gray-500 text-center py-4">
                    No available collectors
                  </p>
                )}
              </div>
              <div className="flex justify-end space-x-2 mt-4">
                <button
                  onClick={() => {
                    setShowAssignModal(false);
                    setSelectedReport(null);
                  }}
                  className="btn-secondary"
                >
                  Cancel
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default ReportManagement;
